(10, 'Ức gà nhồi nấm chiên', 299000, 'uc-ga-nhoi-nam-chien-thom-beo-la-mieng.jpg', '<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"font-size:13.5pt\"><span style=\"color:#333333\">Thịt gà có lẽ đã là một nguyên liệu quá quen thuộc với mọi gia đình rồi, thử biến hóa một chút với công thức ức gà nhồi nấm chiên kiểu Pháp cùng daubepgiadinh.vn xem sao nhé.</span></span></span></span></span></p>\n\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><img alt=\"Ức gà nhồi nấm thơm béo lạ miệng\" src=\"data:image/jpeg;base64,', '20/01/2023', '30 phút', 2, NULL);
